import './App.css';
import uniqid from 'uniqid'
import Card from './components/Card';
import Navigation from './components/Navigation';
import { useState } from 'react';

function App() {
  const [cards, setCards] = useState([
    {title: "Round Pizza", imgSource: "roundPizza", id: uniqid()},
    {title: "Ice Pizza", imgSource: "roundPizza", id: uniqid()},
    {title: "Peasent Pizza", imgSource: "roundPizza", id: uniqid()},
    {title: "Hot Pizza", imgSource: "roundPizza", id: uniqid()},
    {title: "Round Pizza", imgSource: "roundPizza", id: uniqid()},
    {title: "Round Pizza", imgSource: "roundPizza", id: uniqid()},
    {title: "Round Pizza", imgSource: "roundPizza", id: uniqid()},
    {title: "Round Pizza", imgSource: "roundPizza", id: uniqid()},
    {title: "Round Pizza", imgSource: "roundPizza", id: uniqid()},
    {title: "Round Pizza", imgSource: "roundPizza", id: uniqid()},
    {title: "Round Pizza", imgSource: "roundPizza", id: uniqid()},
    {title: "Round Pizza", imgSource: "roundPizza", id: uniqid()},
  ]);

    const cardsClicked = [];

  const cardClicked = (card) => {
    if(cardsClicked.includes(card)) {
      console.log('Game over');
      return
    }
    cardsClicked.push(card)
    setCards(
      [...cards].sort((a,b) => 0.5 - Math.random())
    )
  }

  return (
    <div className="App">
      <Navigation />
      <div id="mainContainer">
          {
            cards.map(card =>  <Card key={card.id} title={card.title} imgSource={card.imgSource} cardClicked={cardClicked}/>)
          }
      </div>
    </div>
  );
}

export default App;
